import React, {Component} from "react";
import axios from 'axios'
import Parser from 'html-react-parser';
import { Checkbox } from 'pretty-checkbox';

const validEmailRegex = RegExp(/^(([^<>()\[\]\.,;:\s@\"]+(\.[^<>()\[\]\.,;:\s@\"]+)*)|(\".+\"))@(([^<>()[\]\.,;:\s@\"]+\.)+[^<>()[\]\.,;:\s@\"]{2,})$/i);
const validateForm = (errors) => {
  let valid = true;
  Object.values(errors).forEach(
    (val) => val.length > 0 && (valid = false)
  );
  return valid;
}


class Main extends Component
{
    constructor(props)
    {
        super(props);

        this.state = {
            headerImage: '',
            artwork_content: '',
            artworks: [],
            artist_image: '',
            artist_content: '',
            email_content: '',

            phone_number: null,
            email: null,
            name: null,
            message: null,
            errors: {
              phone_number: '',
              name: '',
              email: '',
              message: '',
            }
        }
    }

        componentDidMount()
    {
        axios.get('http://127.0.0.1:8000/home/').then(response => {
            let data = response.data.data
            this.setState({
                    headerImage: data.header_image,
                    artwork_content: data.artwork_content,
                    artworks: data.artworks,
                    artist_image: data.artist_image,
                    artist_content:  data.artist_content,
                    email_content:   data.email_content,
                })
            }).catch(error=>{

        })

    }


      handleChange = (event) => {
        event.preventDefault();
        const { name, value } = event.target;
        let errors = this.state.errors;

        switch (name) {
          case 'fullName':
            errors.fullName =
              value.length < 5
                ? 'Full Name must be 5 characters long!'
                : '';
            break;
          case 'email':
            errors.email =
              validEmailRegex.test(value)
                ? ''
                : 'Email is not valid!';
            break;
          case 'password':
            errors.password =
              value.length < 8
                ? 'Password must be 8 characters long!'
                : '';
            break;
          default:
            break;
        }

        this.setState({errors, [name]: value});
    }

    handleSubmit = (event) => {
      event.preventDefault();
      if(validateForm(this.state.errors)) {
        console.info('Valid Form')
      }else{
        console.error('Invalid Form')
      }
    }

    render()
    {
        const {headerImage,artwork_content, artworks, artist_image, artist_content, email_content , errors} = this.state

        return (
            <div className ="wrapper home">
                    <section className="main-banner">
                        <div className="parallax-container">
                            <img src= {headerImage} className="cover-bg" alt="background"/>
                        </div>
                        <h2 className="generic-title"><img src="logo.svg" className="logo"/></h2>

                        <nav className="navigation top-menu">
                            <a className="navigation__link" href="#1">ARTWORK</a>
                            <a className="navigation__link" href="#2">ABOUT</a>
                            <a className="navigation__link" href="#3">CONNECT</a>
                        </nav>
                    </section>
            <section className="container grid parallax-container page-section hero" id="1">
                    <nav className="navigation menu">
                        <a className="navigation__link" href="#1">ARTWORK</a>
                        <a className="navigation__link" href="#2">ABOUT</a>
                        <a className="navigation__link" href="#3">CONNECT</a>
                    </nav>
                    <div className="heading">
                        {Parser(artwork_content)}
                    </div>
                    <div className="grid-inner">
                        {
                              artworks.map(artwork =>  <div>
                                <img src={artwork.image}  className="img-fluid"/>   <p>{artwork.name} </p>
                                </div>)

                        }
                    </div>
                </section>
                <section className="container page-section parallax-container  hero" id="2">
                    <nav className="navigation menu">
                        <a className="navigation__link" href="#1">ARTWORK</a>
                        <a className="navigation__link" href="#2">ABOUT</a>
                        <a className="navigation__link" href="#3">CONNECT</a>
                    </nav>
                </section>


    <section className="middle-section parallax-container">
        <div className="row">
            <div className="col-lg-5 col-md-4">
                <img src= {artist_image} className="author-img" alt="author" />

                <p className="photo-copyright">© Photo by <a href="#">Michel Lunardelli</a></p>
            </div>
            <div className="col-lg-7 col-md-8 description-right">
                  {Parser(artist_content)}
            </div>
        </div>
    </section>
    <section className="container page-section hero" id="3">
        <nav className="navigation menu" id="mainNav">
            <a className="navigation__link" href="#1">ARTWORK</a>
            <a className="navigation__link" href="#2">ABOUT</a>
            <a className="navigation__link" href="#3">CONNECT</a>
        </nav>
    </section>
    <section className="container parallax-container form-banner  hero">
        <div className="heading">
