import React, {Component} from "react";
import axios from 'axios'
import Parser from 'html-react-parser';
import { Checkbox } from 'pretty-checkbox';
import PhoneInput from 'react-phone-input-2'
import 'react-phone-input-2/lib/style.css'
import alertify from 'alertifyjs';
import { Parallax } from 'react-scroll-parallax';
import $ from 'jquery'
import Artworks from "./Artworks";
import Artist from "./Artist";

const validEmailRegex = RegExp(/^(([^<>()\[\]\.,;:\s@\"]+(\.[^<>()\[\]\.,;:\s@\"]+)*)|(\".+\"))@(([^<>()[\]\.,;:\s@\"]+\.)+[^<>()[\]\.,;:\s@\"]{2,})$/i);
const validateForm = (errors) => {
  let valid = true;
  Object.values(errors).forEach(
    (val) => val.length > 0 && (valid = false)
  );
  return valid;
}




class Main extends Component
{
    constructor(props)
    {
        super(props);

        this.state = {
            headerImage: '',
            artwork_content: '',
            artworks: [],
            artist_image: '',
            artist_content: '',
            email_content: '',

            phone_number: null,
            email: null,
            name: null,
            message: null,
            original_work_by_artist: false,
            limited_edition_prints_sculpture: false,
            newsletter_from_artist: false,
            errors: {
              phone_number: '',
              name: '',
              email: '',
              message: '',
            }
        }
    }

        componentDidMount()
    {
        axios.get('http://127.0.0.1:8000/home/').then(response => {
            let data = response.data.data
            this.setState({
                    headerImage: data.header_image,
                    artwork_content: data.artwork_content,
                    artworks: data.artworks,
                    artist_image: data.artist_image,
                    artist_content:  data.artist_content,
                    email_content:   data.email_content,
                })
            }).catch(error=>{

        })

           $(window).scroll( function()
           {
               $('.parallax-container img').each( function(i){
                   var scrollTop     = $(window).scrollTop() + 0;
                   var elementOffset = $(this).offset().top;
                   if( scrollTop > elementOffset ){
                       $(this).css("opacity", 1 - (scrollTop - elementOffset)/786);
                   }
               });
           });

            $('a').bind('click', function(e) {
                    e.preventDefault(); // prevent hard jump, the default behavior
                    var target = $(this).attr("href"); // Set the target as variable

                    // perform animated scrolling by getting top-position of target-element and set it as scroll target
                    $('html, body').stop().animate({
                        scrollTop: $(target).offset().top
                    }, 600, function() {
                        //location.hash = target; //attach the hash (#jumptarget) to the pageurl
                    });

                return false;
           });


            $(window).scroll(function() {
                var scrollDistance = $(window).scrollTop();

                // Assign active class to nav links while scolling
                $('.page-section').each(function(i) {
                    if ($(this).position().top <= scrollDistance) {
                        $('.navigation a.active').removeClass('active');
                        $('.navigation a').eq(i).addClass('active');
                    }
                });
            }).scroll();

    }


    handleChangePhone = (event) => {
        this.setState({
            phone_number: event
        })
    }

    changeCheckbox = (event) => {
        const { name, value } = event.target;
        this.setState({ [name]: value});
    }

    handleChange = (event) => {
        event.preventDefault();
        const { name, value } = event.target;
        let errors = this.state.errors;

        switch (name) {
          case 'name':
            errors.name =
              value.length < 5
                ? 'Name must be 5 characters long!'
                : '';
            break;
          case 'email':
            errors.email =
              validEmailRegex.test(value)
                ? ''
                : 'Email is not valid!';
            break;
          default:
            break;
        }

        this.setState({errors, [name]: value});
    }

    handleSubmit = (event) => {
      event.preventDefault();
      if(validateForm(this.state.errors)) {
           event.preventDefault()
           let fileds = {
              'email':'Email',
              'phone_number':'Phone Number',
              'name':'Name',
              'message':'Message',
           }


           axios.post('http://127.0.0.1:8000/util/contact/', this.state).then(response => {
                alertify.success(response.data.message)
                document.getElementById("contact").reset();
            })
                .catch(error => {
                    let data = error.response.data
                    Object.keys(data).map((keyName, i) => (

                        Object.keys(data[keyName]).map((keyName2, s) => (
                             alertify.error('<div class="label-alertify">'+fileds[keyName]+'</div> '+data[keyName][keyName2])
                        ))
                    ))
           })
      }
    }

