import React, {Component} from "react";
import { useRouteMatch } from "react-router-dom";
import Main from "./Main";
import '../css/bootstrap.min.css'
import '../css/animate.min.css'
import '../css/slick.css'
import '../css/style.css'
import '../css/font-awesome.min.css'
import '../css/alertify.min.css'
import '../css/main.css'
import '../js/slick.js'
import axios from "axios";
import {url_back} from "./Consts";
import Artworks from "./Artworks";
import Galleries from "./Galleries";
import $ from "jquery";


class Gallery extends Component
{
    constructor(props) {
        super(props);

        this.state = {
            id: null,
            galleries: [],
        }
    }

    render()
    {
        this.id =   parseInt(this.props.match.params.id, 10)
        if (!this.id) {
             throw new Error();
         }

        const {galleries} = this.state

        return (
            <section className="gallery-banner">
                 <header>
                     <nav role="navigation" className="top-menu">
                         <div id="menuToggle">
                             <img src="/images/rblogo-white.svg" className="mobile-logo" alt="logo"/>

                             <div className="dotes">
                                 <i className="fa fa-circle"></i>
                                 <i className="fa fa-circle"></i>
                                 <i className="fa fa-circle"></i>
                             </div>
                             <input type="checkbox"/>
                             <ul id="menu-panel">
                                 <a className="navigation__link active" href="/#1">ARTWORK</a>
                                 <a className="navigation__link" href="/#2">ABOUT</a>
                                 <a className="navigation__link" href="/#1">CONNECT</a>
                             </ul>
                         </div>
                         <button type="button" className="close" data-dismiss="menuToggle" aria-label="Close">
                             <span aria-hidden="true">×</span>
                         </button>
                     </nav>
                 </header>
                 <div className="container">
                     <div className="wrapper">
                         <a className="full_screen_modal_close">X</a>
                                   <div className="carousel" id="carousel-page">
                /* {
                                     galleries.map(gallery =>

                            <div key={gallery.id}>
                                   <img className="d-block" src="/images/slider-item4.jpg" alt=" First slide"/>

                                   <div className="art-details">
                                       <h2 className="art-name"><span>Artwork Name,</span> 2020 </h2>

                                       <p> Oil on canvas,</p>

                                       <p>61 x 50.8 cm</p>

                                       <p>(24 x 20 in.)</p>
                                   </div>
                            </div>
                        )

                    }*/

                <div>
                    <img className="d-block" src="/images/slider-item4.jpg" alt=" First slide"/>

                    <div className="art-details">
                        <h2 className="art-name"><span>Artwork Name,</span> 2020 </h2>

                        <p> Oil on canvas,</p>

                        <p>61 x 50.8 cm</p>

                        <p>(24 x 20 in.)</p>
                    </div>
                </div>
           </div>
                     </div>
                 </div>
                 <div className="content-description fixed-bar animated-quick fadeInDown animated">
                     <h2>Ordinary Beauty</h2>


                     <p className="text-center">Bissell’s landscape paintings often illustrate the dichotomy between the natural
                         environment and the creations of humans. We are invited into a meditation and empathy* for the
                         landscape
                         that is presented to us. The scenes we are shown are familiar to us— these paintings ask us to pause
                         and
                         observe what is often seen as the mundane and the ordinary. Bissell then shows us that there is a
                         magic
                         and life to those things we routinely consider deadpan. Everyday details we might routinely pass by
                         are
                         highlighted by his compositional approach and brushwork. And while these works may appear as
                         photographic realism, they transcend the limitations of the photographic form. We not only see into
                         our
                         own narratives but feel empathy for the extraordinary world at our front door.

                     </p>

                     <p className="text-center">
                         "Despite its linguistic roots in ancient Greek, the original concept of empathy is of recent
                         intellectual heritage and comes from work by the German philosopher, Theodor Lipps. The concept
                         began by
                         describing the role it plays in the appreciation of art objects. Lipps originated the then-radical
                         hypothesis that the power of the impact of empathy didn’t reside in the work of art itself but was,
                         rather, synthesized by the viewer in the act of viewing or ‘inseeing’. The moment a viewer
                         recognizes a
                         painting as beautiful, it transforms from an object into a work of art. The act of looking, then,
                         becomes a creative process, and the viewer becomes the artist" —Robert Bissell

                     </p>

                     <p className="text-center details-txt">
                         *A word on empathy and the art of ‘in-seeing’:</p>
                 </div>
                 <p className="container copyright"> Copyright © 2020 Robert Bissell.<span>  All rights reserved.</span></p>
                <Main />
            </section>

        )
    }

        componentDidMount()
    {
        axios.get(url_back+'/util/gallery/'+this.id).then(response => {
            let data = response.data.data
            this.setState({
                    galleries: data.galleries,
                })
            }).catch(error=>{
        })

                $(window).scroll(function () {
            $('.parallax-container').each(function (i) {
                var scrollTop = $(window).scrollTop() + 0;
                var elementOffset = $(this).offset().top;
                if (scrollTop > elementOffset) {
                    $(this).css("opacity", 1 - (scrollTop - elementOffset) / 786);
                }
            });
        });
        if ($(window).width() > 767) {
            $('.page-section').addClass('parallax-container');
        } else {
            $('.page-section').removeClass('parallax-container');
        }


        $(window).scroll(function () {
            var scrollTop = $(window).scrollTop();
            if (scrollTop > 900) {
                $('.header-sticky').addClass('fadeInDown');
                $('.header-sticky').removeClass('fadeOutUp');
                $('.header-sticky').addClass('animated');
            } else {
                $('.header-sticky').addClass('fadeOutUp');
                $('.header-sticky').removeClass('fadeInUp');
                $('.header-sticky').removeClass('animated');
            }
        });

        $(window).scroll(function () {
            var scrollTop = $(window).scrollTop();
            if (scrollTop > 10) {
                $('.content-description').addClass('fadeInDown');
                $('.content-description').removeClass('fadeOutUp');
                $('.content-description').addClass('animated');
            } else {
                $('.content-description').addClass('fadeOutUp');
                $('.content-description').removeClass('fadeInUp');
                //$('.content-description').removeClass('animated');
            }
        });


        $("#menuToggle").click(function () {
            $("#menu1").fadeIn();
        });
        $(".close").click(function () {
            $("#menu1").fadeOut();
        });

        initSlick();


        $(".full_screen_modal_close").click(function () {
            $(".full_screen_modal_close").hide();
            $(".wrapper").addClass('test');
            $(".wrapper").removeClass('full_screen');
            $("#carousel-page").slick('unslick');
            initSlick();
        });

        function initSlick() {
            if($("#carousel-page").length){
              $("#carousel-page").slick({
                infinite: true,
                slidesToShow: 3,
                slidesToScroll: 3,
                responsive: [

                    {
                        breakpoint: 1024,
                        settings: {
                            slidesToShow: 2,
                            slidesToScroll: 2
                        }
                    },
                    {
                        breakpoint: 767,
                        settings: {
                            slidesToShow: 1,
                            slidesToScroll: 1
                        }
                    }
                ]
            });
            }


            $(".slick-slide").click(function (event) {
                var currentIndex = $(this).attr('data-slick-index');

                $("#carousel-page").slick('unslick');
                $(".wrapper").addClass('full_screen');
                $(".full_screen_modal_close").show();
                $("#carousel-page").slick({
                    infinite: true,
                    slidesToShow: 1,
                    fade: true,
                    speed: 300,
                    cssEase: 'cubic-bezier(0.7, 0, 0.3, 1)',
                    touchThreshold: 100
                });
            });
        }
    }
}

export default Gallery
