import React, {Component} from "react";
import axios from "axios";
import {url_back} from "./Consts";
import $ from "jquery";

class Galleries extends Component
{
    constructor(props) {
        super(props);
    }

    render()
    {
        let galleries = this.props.galleries

        return (
           <div className="carousel" id="carousel-page">
                 {
                                     galleries.map(gallery =>

                            <div key={gallery.id}>
                                   <img className="d-block" src="/images/slider-item4.jpg" alt=" First slide"/>

                                   <div className="art-details">
                                       <h2 className="art-name"><span>Artwork Name,</span> 2020 </h2>

                                       <p> Oil on canvas,</p>

                                       <p>61 x 50.8 cm</p>

                                       <p>(24 x 20 in.)</p>
                                   </div>
                            </div>
                        )

                    }

                /*<div>
                    <img className="d-block" src="/images/slider-item4.jpg" alt=" First slide"/>

                    <div className="art-details">
                        <h2 className="art-name"><span>Artwork Name,</span> 2020 </h2>

                        <p> Oil on canvas,</p>

                        <p>61 x 50.8 cm</p>

                        <p>(24 x 20 in.)</p>
                    </div>
                </div>*/
           </div>
        )
    }

            componentDidMount()
    {
        initSlick();


        $(".full_screen_modal_close").click(function () {
            $(".full_screen_modal_close").hide();
            $(".wrapper").addClass('test');
            $(".wrapper").removeClass('full_screen');
            $("#carousel-page").slick('unslick');
            initSlick();
        });

        function initSlick() {
            if($("#carousel-page").length){
              $("#carousel-page").slick({
                infinite: true,
                slidesToShow: 3,
                slidesToScroll: 3,
                responsive: [

                    {
                        breakpoint: 1024,
                        settings: {
                            slidesToShow: 2,
                            slidesToScroll: 2
                        }
                    },
                    {
                        breakpoint: 767,
                        settings: {
                            slidesToShow: 1,
                            slidesToScroll: 1
                        }
                    }
                ]
            });
            }


            $(".slick-slide").click(function (event) {
                var currentIndex = $(this).attr('data-slick-index');

                $("#carousel-page").slick('unslick');
                $(".wrapper").addClass('full_screen');
                $(".full_screen_modal_close").show();
                $("#carousel-page").slick({
                    infinite: true,
                    slidesToShow: 1,
                    fade: true,
                    speed: 300,
                    cssEase: 'cubic-bezier(0.7, 0, 0.3, 1)',
                    touchThreshold: 100
                });
            });
        }
    }
}

export default Galleries
