import React, {Component} from "react";
import axios from 'axios'

class Main extends Component
{
    constructor(props)
    {
        super(props);

        this.state = {
            headerImage: '',
            artwork_content: '',
            artworks: [],
            artist_image: '',
            artist_content: '',
            email_content: '',
        }
    }

        componentDidMount()
    {
        axios.get('http://127.0.0.1:8000/home/').then(response => {
            let data = response.data

            this.setState({
                    headerImage: data.header_image,
                    artwork_content: data.artwork_content,
                    artworks: data.artworks,
                    artist_image: data.artist_image,
                    artist_content:  data.artist_content,
                    email_content:   data.email_content,
                })
            }).catch(error=>{

        })
    }

    render()
    {
        const {headerImage,artwork_content, artworks, artist_image, artist_content, email_content } = this.state
        return (
            <div className="wrapper home">
                    <section class="main-banner">
                        <div class="parallax-container">
                            <img src="images/generic-bg.png" class="cover-bg" alt="background"/>
                        </div>
                        <h2 class="generic-title"><img src="images/logo.svg" class="logo"/></h2>

                        <nav class="navigation top-menu">
                            <a class="navigation__link" href="#1">ARTWORK</a>
                            <a class="navigation__link" href="#2">ABOUT</a>
                            <a class="navigation__link" href="#3">CONNECT</a>
                        </nav>
                    </section>
            </div>
        )
    }
}

export default Main
