import * as React from 'react';
import { render } from '@testing-library/react';
import { PrettyLabel } from '../PrettyLabel';
describe('PrettyLabel tests', () => {
    it('should render without errors', () => {
        expect(() => {
            render(React.createElement(PrettyLabel, null)).container;
        }).not.toThrow();
    });
    it('should render children inside the label', () => {
        const { getByText, container } = render(React.createElement(PrettyLabel, { baseId: "abc" }, "Testing"));
        getByText('Testing');
        expect(container).toMatchSnapshot();
    });
    it('should append colors to the root div', () => {
        expect(render(React.createElement(PrettyLabel, { color: "danger" }, "Testing")).container).toMatchSnapshot();
    });
});
